INSERT INTO `products` (`id`, `name`, `alias`, `price`, `pricesale`, `intro`, `content`, `image`, `keywork`, `discription`, `user_id`, `cate_id`, `created_at`, `updated_at`) VALUES
(2, 'LOFFICIEL VN (SỐ 5)', 'lofficiel-vn-so-5', 49000, 35000, '<p>\r\n	1</p>\r\n', '<p>\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/cong-chua-mat-trang-tap-3-cress-toc-may-phan-2--1-.jpg" style="width: 371px; height: 480px;" />1</p>\r\n', 'ao-trang-tap-10-2015-giang-ho-vat--1-.jpg', 'LOFFICIEL VN (SỐ 5)', 'LOFFICIEL VN (SỐ 5)', 1, 1, '2016-02-03 02:55:50', '2016-02-03 22:51:10'),
(3, 'NHÀ GIẢ KIM', 'nha-gia-kim', 59000, 41000, '<p>\r\n	<span style="color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">Tất cả những trải nghiệm trong chuyến phi&ecirc;u du theo đuổi vận mệnh của m&igrave;nh đ&atilde; gi&uacute;p Santiago thấu hiểu được &yacute; nghĩa s&acirc;u xa nhất của hạnh ph&uacute;c, h&ograve;a hợp với vũ trụ v&agrave; con người. Tiểu thuyết Nh&agrave; giả kim của Paulo Coelho như một c&acirc;u chuyện cổ t&iacute;ch giản dị, nh&acirc;n &aacute;i, gi&agrave;u chất thơ, thấm đẫm những minh triết huyền b&iacute; của phương Đ&ocirc;ng. Trong lần xuất bản đầu ti&ecirc;n tại Brazil v&agrave;o năm 1988, s&aacute;ch chỉ b&aacute;n được 900 bản. Nhưng, với số phận đặc biệt của cuốn s&aacute;ch d&agrave;nh cho to&agrave;n nh&acirc;n...</span></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Tất cả những trải nghiệm trong chuyến phi&ecirc;u du theo đuổi vận mệnh của m&igrave;nh đ&atilde; gi&uacute;p Santiago thấu hiểu được &yacute; nghĩa s&acirc;u xa nhất của hạnh ph&uacute;c, h&ograve;a hợp với vũ trụ v&agrave; con người.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Tiểu thuyết Nh&agrave; giả kim của Paulo Coelho như một c&acirc;u chuyện cổ t&iacute;ch giản dị, nh&acirc;n &aacute;i, gi&agrave;u chất thơ, thấm đẫm những minh triết huyền b&iacute; của phương Đ&ocirc;ng. Trong lần xuất bản đầu ti&ecirc;n tại Brazil v&agrave;o năm 1988, s&aacute;ch chỉ b&aacute;n được 900 bản. Nhưng, với số phận đặc biệt của cuốn s&aacute;ch d&agrave;nh cho to&agrave;n nh&acirc;n loại, vượt ra ngo&agrave;i bi&ecirc;n giới quốc gia, Nh&agrave; giả kim đ&atilde; l&agrave;m rung động h&agrave;ng triệu t&acirc;m hồn, trở th&agrave;nh một trong những cuốn s&aacute;ch b&aacute;n chạy nhất mọi thời đại, v&agrave; c&oacute; thể l&agrave;m thay đổi cuộc đời người đọc.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/nha-gia-kim--1-.jpg" style="width: 371px; height: 480px;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	&ldquo;Nhưng nh&agrave; luyện kim đan kh&ocirc;ng quan t&acirc;m mấy đến những điều ấy. &Ocirc;ng đ&atilde; từng thấy nhiều người đến rồi đi, trong khi ốc đảo v&agrave; sa mạc vẫn l&agrave; ốc đảo v&agrave; sa mạc. &Ocirc;ng đ&atilde; thấy vua ch&uacute;a v&agrave; kẻ ăn xin đi qua biển c&aacute;t n&agrave;y, c&aacute;i biển c&aacute;t thường xuy&ecirc;n thay h&igrave;nh đổi dạng v&igrave; gi&oacute; thổi nhưng vẫn m&atilde;i m&atilde;i l&agrave; biển c&aacute;t m&agrave; &ocirc;ng đ&atilde; biết từ thuở nhỏ. Tuy vậy, tự đ&aacute;y l&ograve;ng m&igrave;nh, &ocirc;ng kh&ocirc;ng thể kh&ocirc;ng cảm thấy vui trước hạnh ph&uacute;c của mỗi người lữ kh&aacute;ch, sau bao ng&agrave;y chỉ c&oacute; c&aacute;t v&agrave;ng với trời xanh nay được thấy ch&agrave; l&agrave; xanh tươi hiện ra trước mắt. &lsquo;C&oacute; thể Thượng đế tạo ra sa mạc chỉ để cho con người biết qu&yacute; trọng c&acirc;y ch&agrave; l&agrave;,&rsquo; &ocirc;ng nghĩ.&rdquo;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/nha-gia-kim--2-.jpg" style="width: 371px; height: 480px;" /></p>\r\n', 'nha-gia-kim--1-.jpg', 'NHÀ GIẢ KIM', 'NHÀ GIẢ KIM', 1, 1, '2016-02-03 23:28:34', '2016-02-03 23:28:34'),
(4, 'NẾU GẶP NGƯỜI ẤY', 'neu-gap-nguoi-ay', 86000, 65000, '<p>\r\n	<span style="color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">Cả hai im lặng một l&uacute;c, t&ocirc;i nghe được cả tiếng ro ro của m&aacute;y n&eacute;n kh&iacute; khi thổi &ocirc; xi v&agrave;o bể thủy sinh. Xung quanh phảng phất thứ m&ugrave;i như m&ugrave;i của khu rừng vừa tạnh mưa. &ldquo;N&agrave;y, Satoshi.&rdquo; Karin gọi t&ecirc;n t&ocirc;i. T&ocirc;i c&oacute; cảm gi&aacute;c như chỗ mỏng nhất của lớp da bao bọc tr&aacute;i tim bị r&aacute;ch to&aacute;c, một thứ g&igrave; đ&oacute; vừa được giải ph&oacute;ng ra. &ldquo;G&igrave; vậy?&rdquo; Giọng t&ocirc;i run run. &ldquo;Gặp được cậu tớ vui lắm. Tớ chỉ muốn n&oacute;i như vậy th&ocirc;i.&rdquo; ...</span></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Cả hai im lặng một l&uacute;c, t&ocirc;i nghe được cả tiếng ro ro của m&aacute;y n&eacute;n kh&iacute; khi thổi &ocirc; xi v&agrave;o bể thủy sinh. Xung quanh phảng phất thứ m&ugrave;i như m&ugrave;i của khu rừng vừa tạnh mưa.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	&ldquo;N&agrave;y, Satoshi.&rdquo;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Karin gọi t&ecirc;n t&ocirc;i. T&ocirc;i c&oacute; cảm gi&aacute;c như chỗ mỏng nhất của lớp da bao bọc tr&aacute;i tim bị r&aacute;ch to&aacute;c, một thứ g&igrave; đ&oacute; vừa được giải ph&oacute;ng ra.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	&ldquo;G&igrave; vậy?&rdquo; Giọng t&ocirc;i run run.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	&ldquo;Gặp được cậu tớ vui lắm. Tớ chỉ muốn n&oacute;i như vậy th&ocirc;i.&rdquo;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/neu-gap-nguoi-ay-cho-toi-gui-loi-chao--1-.jpg" style="width: 371px; height: 480px;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Takuji Ichikawa sinh ng&agrave;y m&ugrave;ng 7 th&aacute;ng 10 năm 1962 tại Tokyo. &Ocirc;ng tốt nghiệp trường đại học Dokkyo. Cuốn tiểu thuyết đầu tay của &ocirc;ng, Separation, xuất bản năm 2002.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Cuốn Em sẽ đến c&ugrave;ng cơn mưa (xuất bản tại Nhật với t&ecirc;n Ima Ainiyukimasu) in lần đầu năm 2003, l&agrave; một trong những tiểu thuyết th&agrave;nh c&ocirc;ng nhất Nhật Bản, được chuyển thể th&agrave;nh truyện tranh, kịch, phim truyền h&igrave;nh ở Nhật v&agrave; điện ảnh tại Mỹ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/neu-gap-nguoi-ay-cho-toi-gui-loi-chao--2-.jpg" style="width: 371px; height: 480px;" /></p>\r\n', 'neu-gap-nguoi-ay-cho-toi-gui-loi-chao--1-.jpg', 'NẾU GẶP NGƯỜI ẤY', 'NẾU GẶP NGƯỜI ẤY', 1, 2, '2016-02-03 23:31:08', '2016-02-03 23:31:08'),
(5, 'THỜI ÁO TRẮNG', 'thoi-ao-trang', 35000, 24000, '<p>\r\n	<span style="color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">Qua trang văn của Ho&agrave;ng Mai Quy&ecirc;n, bức tranh tuổi học tr&ograve; miền s&ocirc;ng nước miền T&acirc;y Nam Bộ hiện ra thật dung dị. Đ&oacute; l&agrave; những buổi bơi xuồng h&aacute;i b&ocirc;ng đi&ecirc;n điển, l&agrave; những ng&agrave;y vớt c&aacute; linh đầy ắp khoang xuồng v&agrave;o m&ugrave;a nước nổi, l&agrave; những buổi đi đặt lọp, bắt t&ocirc;m - c&aacute;, l&agrave; mỗi ng&agrave;y phải đi ngang cầu khỉ bấp b&ecirc;nh... G&aacute;nh nặng mưu sinh nhọc nhằn kh&ocirc;ng l&agrave;m mất đi sự tinh nghịch của tuổi học tr&ograve;. Sự ch&ecirc;nh lệch kinh tế, gia cảnh ng&agrave;y c&agrave;ng k&eacute;o c&aacute;c em lại...</span></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Qua trang văn của Ho&agrave;ng Mai Quy&ecirc;n, bức tranh tuổi học tr&ograve; miền s&ocirc;ng nước miền T&acirc;y Nam Bộ hiện ra thật dung dị. Đ&oacute; l&agrave; những buổi bơi xuồng h&aacute;i b&ocirc;ng đi&ecirc;n điển, l&agrave; những ng&agrave;y vớt c&aacute; linh đầy ắp khoang xuồng v&agrave;o m&ugrave;a nước nổi, l&agrave; những buổi đi đặt lọp, bắt t&ocirc;m - c&aacute;, l&agrave; mỗi ng&agrave;y phải đi ngang cầu khỉ bấp b&ecirc;nh...</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	G&aacute;nh nặng mưu sinh nhọc nhằn kh&ocirc;ng l&agrave;m mất đi sự tinh nghịch của tuổi học tr&ograve;. Sự ch&ecirc;nh lệch kinh tế, gia cảnh ng&agrave;y c&agrave;ng k&eacute;o c&aacute;c em lại gần nhau hơn. Tất cả l&agrave; những kỷ niệm đ&aacute;ng nhớ thời &aacute;o trắng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/van-hoc-teen-thoi-ao-trang--1-.jpg" style="width: 371px; height: 480px;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Cũng l&agrave; đề t&agrave;i thời học sinh nhưng lại khai th&aacute;c c&acirc;u chuyện theo một kh&iacute;a cạnh ho&agrave;n to&agrave;n kh&aacute;c. C&oacute; lẽ một phần l&agrave; do t&aacute;c giả kh&ocirc;ng c&ugrave;ng thời với lứa học sinh b&acirc;y giờ n&ecirc;n văn phong nhẹ nh&agrave;ng, trong s&aacute;ng, đậm chất th&ocirc;n qu&ecirc;. Kh&ocirc;ng c&oacute; những chi tiết qu&aacute; hiện đại, thay v&agrave;o đ&oacute; l&agrave; những h&igrave;nh ảnh b&igrave;nh dị của một thời đ&atilde; xa. Đọc truyện ta như thấm dần t&acirc;m hồn v&agrave;o mạch cảm x&uacute;c truyện. Những nh&acirc;n vật truyện kh&ocirc;ng như những học sinh b&acirc;y giờ, họ phải lo toan những chuyện mưu sinh.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/van-hoc-teen-thoi-ao-trang--2-.jpg" style="width: 371px; height: 480px;" /></p>\r\n', 'van-hoc-teen-thoi-ao-trang--1-.jpg', 'THỜI ÁO TRẮNG', 'THỜI ÁO TRẮNG', 1, 7, '2016-02-03 23:35:46', '2016-02-03 23:35:46'),
(6, 'ĐI QUA HOA CÚC', 'di-qua-hoa-cuc', 56000, 50000, '<p>\r\n	<span style="color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">Cuốn Đi Qua Hoa C&uacute;c l&agrave; tập truyện d&agrave;i của Nguyễn Nhật &Aacute;nh, mở đầu c&acirc;u truyện t&aacute;c giả kể lại tuổi ấu thơ hồn nhi&ecirc;n của nh&acirc;n vật trong truyện, kết hợp với tả cảnh ở miền qu&ecirc;, những ng&ocirc;i nh&agrave; nằm dọc hai b&ecirc;n đường đ&aacute; sỏi dọc theo hai b&ecirc;n h&agrave;ng d&acirc;m bụt v&agrave; cả c&acirc;y sứ c&acirc;y b&agrave;ng tỏa b&oacute;ng m&aacute;t, tỏa hương thơm trước s&acirc;n nh&agrave;. Một n&eacute;t vẽ n&ecirc;n thơ thật đầm ấm ở một v&ugrave;ng qu&ecirc; xa x&ocirc;i t&aacute;c giả dường như l&agrave;m ấm l&ograve;ng cho người đọc. Thật vậy mỗi cốt...</span></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Cuốn Đi Qua Hoa C&uacute;c l&agrave; tập truyện d&agrave;i của Nguyễn Nhật &Aacute;nh, mở đầu c&acirc;u truyện t&aacute;c giả kể lại tuổi ấu thơ hồn nhi&ecirc;n của nh&acirc;n vật trong truyện, kết hợp với tả cảnh ở miền qu&ecirc;, những ng&ocirc;i nh&agrave; nằm dọc hai b&ecirc;n đường đ&aacute; sỏi dọc theo hai b&ecirc;n h&agrave;ng d&acirc;m bụt v&agrave; cả c&acirc;y sứ c&acirc;y b&agrave;ng tỏa b&oacute;ng m&aacute;t, tỏa hương thơm trước s&acirc;n nh&agrave;. Một n&eacute;t vẽ n&ecirc;n thơ thật đầm ấm ở một v&ugrave;ng qu&ecirc; xa x&ocirc;i t&aacute;c giả dường như l&agrave;m ấm l&ograve;ng cho người đọc. Thật vậy mỗi cốt truyện của Nguyễn Nhật &Aacute;nh đ&atilde; ph&aacute;c họa l&ecirc;n một n&eacute;t qu&ecirc; hương ngọt ng&agrave;o, một thời ấu thơ đẹp, một t&igrave;nh y&ecirc;u của tuổi học tr&ograve; cũng h&ograve;a lẫn t&igrave;nh y&ecirc;u kh&aacute;t khao của bao lứa tuổi. Cuốn truyện d&agrave;i Đi Qua Hoa C&uacute;c l&agrave; một trong những t&aacute;c phẩm tuyệt t&aacute;c hay của t&aacute;c giả l&agrave;m th&ocirc;i th&uacute;c người đọc th&ecirc;m nhiều ấn tượng v&agrave; sự l&ocirc;i cuốn tr&agrave;n d&acirc;ng trong l&ograve;ng bạn đọc.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/di-qua-hoa-cuc-tai-ban--1-.jpg" style="width: 371px; height: 480px;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	&quot;Trước nh&agrave; b&agrave; nội t&ocirc;i c&oacute; một c&acirc;y b&agrave;ng cao thật cao. Mỗi lần về thăm nội, khi chiếc xe gobel của ba t&ocirc;i ngoặt quanh c&aacute;i giếng đ&aacute; đầu l&agrave;ng, bao giờ t&ocirc;i cũng nhấp nhổm ở y&ecirc;n sau v&agrave; hồi hộp ngước mắt tr&ocirc;ng l&ecirc;n. So với d&atilde;y h&agrave;ng r&agrave;o d&acirc;m bụt của những ng&ocirc;i nh&agrave; nằm dọc hai b&ecirc;n con đường đ&aacute; sỏi, kể cả ngọn sầu đ&ocirc;ng v&agrave; c&acirc;y sứ trắng toả hương thơm nức mũi trước s&acirc;n nh&agrave; b&agrave; t&ocirc;i l&uacute;c n&agrave;o cũng vươn cao sừng sững. Khi nh&igrave;n l&ecirc;n, hễ thấy t&aacute;n b&agrave;ng xanh um kia hiện ra trong tầm mắt như một chấm đen mỗi l&uacute;c một lớn dần, t&ocirc;i biết ngay đ&atilde; sắp đến nh&agrave; b&agrave;. V&agrave; thế l&agrave; t&ocirc;i kh&ocirc;ng n&eacute;n nổi nụ cười sung sướng. V&agrave; cả e thẹn nữa, chẳng hiểu v&igrave; sao. Những l&uacute;c đ&oacute;, bao giờ t&ocirc;i cũng &uacute;p mặt v&agrave;o lưng ba t&ocirc;i để giấu đi nỗi xao xuyến của m&igrave;nh. Cũng như vậy, trước ng&otilde; nh&agrave; &ocirc;ng ngoại t&ocirc;i l&agrave; một h&agrave;ng r&agrave;o hoa giấy đỏ. Hoa kh&ocirc;ng thẫm, chỉ đỏ hồng. V&igrave; trồng l&acirc;u năm n&ecirc;n c&acirc;y uốn lượn chằng chịt, gốc n&agrave;o gốc nấy to bằng bắp ch&acirc;n người. Hoa rực rỡ từng ch&ugrave;m, từng nh&aacute;nh, phủ k&iacute;n cả hai trụ cổng bằng đ&aacute; ong l&acirc;u ng&agrave;y l&ecirc;n r&ecirc;u xanh mướt. Qu&ecirc; nội t&ocirc;i thuộc một l&agrave;ng miền n&uacute;i. Qu&ecirc; ngoại t&ocirc;i ở miệt đồng bằng. Nh&agrave; &ocirc;ng t&ocirc;i ở c&aacute;ch đường quốc lộ non một c&acirc;y số về ph&iacute;a biển. Nhưng v&igrave; kh&ocirc;ng bị c&acirc;y cối che khuất n&ecirc;n đứng tr&ecirc;n đường người ta vẫn c&oacute; thể tr&ocirc;ng thấy r&otilde; mồn một vừng hoa đỏ ối dưới kia. Sau n&agrave;y, khi đ&atilde; đi xa, mỗi lần về thăm ngoại, t&ocirc;i ngồi tr&ecirc;n xe đ&ograve; băng qua cầu Cẩm Lễ, mắt n&ocirc;n nao ng&oacute;ng về ph&iacute;a biển, hễ thấy hoa đỏ vẫy tay l&agrave; biết đ&atilde; tới nh&agrave;.&quot;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/di-qua-hoa-cuc-tai-ban--2-.jpg" style="width: 371px; height: 480px;" /></p>\r\n', 'di-qua-hoa-cuc-tai-ban--1-.jpg', 'ĐI QUA HOA CÚC', 'ĐI QUA HOA CÚC', 1, 2, '2016-02-03 23:38:23', '2016-02-03 23:38:23'),
(7, 'TRÊN ĐƯỜNG BĂNG', 'tren-duong-bang', 50000, 0, '<p>\r\n	<span style="color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">&quot;Khi c&ograve;n trẻ, h&atilde;y ra ngo&agrave;i nhiều hơn ở nh&agrave;. H&atilde;y nh&agrave;o v&ocirc; xin người kh&aacute;c &ldquo;b&oacute;c hết, lột sạch&rdquo; khả năng của m&igrave;nh. Chỉ sợ bất t&agrave;i nộp hồ sơ &ldquo;xin việc&rdquo;, m&agrave; chả ai th&egrave;m cho, chả ai th&egrave;m b&oacute;c lột. Khi đ&atilde; được b&oacute;c v&agrave; lột hết, d&ugrave; sau n&agrave;y đi đ&acirc;u, l&agrave;m g&igrave;, bạn đều cực kỳ th&agrave;nh c&ocirc;ng. V&igrave; năng lực được trui r&egrave;n trong qu&aacute; tr&igrave;nh l&agrave;m cho người kh&aacute;c. Sự chăm chỉ, t&iacute;nh kỷ luật, quen tay quen ch&acirc;n, quen ng&aacute;p, quen lười&hellip; cũng từ c&ocirc;ng việc m&agrave; ra. Mọi &ocirc;ng chủ...</span></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	&quot;Khi c&ograve;n trẻ, h&atilde;y ra ngo&agrave;i nhiều hơn ở nh&agrave;. H&atilde;y nh&agrave;o v&ocirc; xin người kh&aacute;c &ldquo;b&oacute;c hết, lột sạch&rdquo; khả năng của m&igrave;nh. Chỉ sợ bất t&agrave;i nộp hồ sơ &ldquo;xin việc&rdquo;, m&agrave; chả ai th&egrave;m cho, chả ai th&egrave;m b&oacute;c lột. Khi đ&atilde; được b&oacute;c v&agrave; lột hết, d&ugrave; sau n&agrave;y đi đ&acirc;u, l&agrave;m g&igrave;, bạn đều cực kỳ th&agrave;nh c&ocirc;ng. V&igrave; năng lực được trui r&egrave;n trong qu&aacute; tr&igrave;nh l&agrave;m cho người kh&aacute;c. Sự chăm chỉ, t&iacute;nh kỷ luật, quen tay quen ch&acirc;n, quen ng&aacute;p, quen lười&hellip; cũng từ c&ocirc;ng việc m&agrave; ra. Mọi &ocirc;ng chủ vĩ đại đều từng l&agrave; những người l&agrave;m c&ocirc;ng ở vị tr&iacute; thấp nhất. Họ đều rẽ tr&aacute;i trong khi mọi người rẽ phải. Họ c&oacute; những quyết định kh&ocirc;ng theo đ&aacute;m đ&ocirc;ng, kh&ocirc;ng cam chịu sống một cuộc đời tầm thường, nhạt nh&ograve;a&hellip; rồi chết.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	C&ograve;n những bạn thu nhập 6 triệu cũng t&uacute;ng thiếu, 20 triệu cũng đi vay mượn để ti&ecirc;u d&ugrave;ng, th&igrave; th&ocirc;i, cuộc đời họ chấm dứt giấc mơ lớn. Tiền nong c&aacute; nh&acirc;n quản l&yacute; kh&ocirc;ng được, th&igrave; l&agrave;m sao m&agrave; quản trị t&agrave;i ch&iacute;nh một cơ nghiệp lớn?&rdquo;. Tư duy thế n&agrave;o th&igrave; n&oacute; ra số phận thế đ&oacute;.&quot;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	(Tony buổi s&aacute;ng)</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/tren-duong-bang--1-.jpg" style="width: 371px; height: 480px;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Tr&ecirc;n đường băng l&agrave; tập hợp những b&agrave;i viết được ưa th&iacute;ch tr&ecirc;n Facebook của Tony Buổi S&aacute;ng. Nhưng kh&aacute;c với một tập tản văn th&ocirc;ng thường, nội dung c&aacute;c b&agrave;i được chọn lọc c&oacute; chủ đ&iacute;ch, nhằm chuẩn bị về tinh thần, kiến thức&hellip;cho c&aacute;c bạn trẻ v&agrave;o đời. S&aacute;ch gồm 3 phần: &ldquo;Chuẩn bị h&agrave;nh trang&rdquo;, &ldquo;Trong ph&ograve;ng chờ s&acirc;n bay&rdquo; v&agrave; &ldquo;L&ecirc;n m&aacute;y bay&rdquo;, tương ứng với những qu&aacute; tr&igrave;nh một bạn trẻ phải trải qua trước khi &ldquo;cất c&aacute;nh&rdquo; tr&ecirc;n đường băng cuộc đời, bay v&agrave;o bầu trời cao rộng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Những b&agrave;i viết của Tony sinh động, thiết thực, h&agrave;i hước v&agrave; xuất ph&aacute;t từ c&aacute;i t&acirc;m trong s&aacute;ng của một người đi trước nhiều kinh nghiệm. Anh viết về th&aacute;i độ với sự học v&agrave; kiến thức n&oacute;i chung, c&aacute;ch ứng ph&oacute; với những trắc trở thử th&aacute;ch khi đi l&agrave;m, c&aacute;ch sống h&agrave;o sảng nghĩa t&igrave;nh văn minh&hellip;truyền cảm hứng cho c&aacute;c bạn trẻ sống hết m&igrave;nh, trọn vẹn từng ph&uacute;t gi&acirc;y. Tuy đối tượng độc giả ch&iacute;nh m&agrave; cuốn s&aacute;ch hướng đến l&agrave; c&aacute;c bạn trẻ, nhưng độc giả lớn tuổi hơn vẫn c&oacute; thể đọc s&aacute;ch để hiểu v&agrave; c&oacute; c&aacute;ch hỗ trợ con em m&igrave;nh một c&aacute;ch đ&uacute;ng đắn, chứ kh&ocirc;ng &ldquo;ủ&rdquo; con qu&aacute; kỹ để rồi tạo ra một thế hệ yếu ớt, kh&ocirc;ng biết tự lập. Những người đi l&agrave;m nhiều năm đọc s&aacute;ch cũng c&oacute; thể nh&igrave;n lại con đường đi của m&igrave;nh, tự ngẫm đ&oacute; đ&atilde; phải l&agrave; con đường m&igrave;nh muốn đi chưa, bởi thay đổi kh&ocirc;ng bao giờ l&agrave; qu&aacute; muộn.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/tren-duong-bang--2-.jpg" style="width: 371px; height: 480px;" /></p>\r\n', 'tren-duong-bang--1-.jpg', 'TRÊN ĐƯỜNG BĂNG', 'TRÊN ĐƯỜNG BĂNG', 1, 4, '2016-02-03 23:40:27', '2016-02-03 23:40:27'),
(8, 'PIPPI TẤT DÀI', 'pippi-tat-dai', 78000, 0, '<p>\r\n	<span style="color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">Pippi tất d&agrave;i, một c&aacute;i t&ecirc;n đ&atilde; được h&agrave;ng triệu trẻ em của bao nhi&ecirc;u thế hệ tr&ecirc;n thế giới y&ecirc;u mến. C&ocirc; b&eacute; t&oacute;c đỏ, mặt đầy t&agrave;n nhang tinh qu&aacute;i đ&oacute; l&agrave; giấc mơ sống động m&agrave; c&oacute; lẽ chưa từng một ai kh&ocirc;ng &ocirc;m ấp khi nghĩ về tuổi thơ, trẻ thơ. B&ecirc;n s&acirc;n nh&agrave; của hai anh em Thomas v&agrave; Annika, c&oacute; một Biệt thự t&ecirc;n l&agrave; B&aacute;t Nh&aacute;o nhưng chẳng ai ở cả. Ch&uacute;ng mơ ước c&oacute; h&agrave;ng x&oacute;m với lũ trẻ để chơi c&ugrave;ng biết bao. V&agrave; rồi một c&ocirc; bạn...</span></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Pippi tất d&agrave;i, một c&aacute;i t&ecirc;n đ&atilde; được h&agrave;ng triệu trẻ em của bao nhi&ecirc;u thế hệ tr&ecirc;n thế giới y&ecirc;u mến. C&ocirc; b&eacute; t&oacute;c đỏ, mặt đầy t&agrave;n nhang tinh qu&aacute;i đ&oacute; l&agrave; giấc mơ sống động m&agrave; c&oacute; lẽ chưa từng một ai kh&ocirc;ng &ocirc;m ấp khi nghĩ về tuổi thơ, trẻ thơ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	B&ecirc;n s&acirc;n nh&agrave; của hai anh em Thomas v&agrave; Annika, c&oacute; một Biệt thự t&ecirc;n l&agrave; B&aacute;t Nh&aacute;o nhưng chẳng ai ở cả. Ch&uacute;ng mơ ước c&oacute; h&agrave;ng x&oacute;m với lũ trẻ để chơi c&ugrave;ng biết bao. V&agrave;&nbsp; rồi một c&ocirc; bạn h&agrave;ng x&oacute;m xuất hiện. C&ocirc; b&eacute; 9 tuổi ấy chỉ đi một m&igrave;nh, với một ch&uacute; ngựa v&agrave; một &ocirc;ng khỉ. Ch&uacute;ng đ&atilde; kết th&agrave;nh bạn th&acirc;n với nhau.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Pippi c&oacute; một va ly đầy tiền v&agrave;ng. C&ocirc; b&eacute; kh&ocirc;ng th&iacute;ch đi học, v&igrave; từ nhỏ đ&atilde; l&ecirc;nh đ&ecirc;nh tr&ecirc;n biển với bố n&ecirc;n chẳng biết đi học ở đ&acirc;u. Ước mơ của c&ocirc; b&eacute; l&agrave; tương lai trở th&agrave;nh tướng cướp. V&agrave; c&ocirc; b&eacute; sống ho&agrave;n to&agrave;n tự do, kh&ocirc;ng chịu được g&ograve; b&oacute; n&agrave;o, kh&ocirc;ng ai bắt c&ocirc; đi ngủ đ&uacute;ng giờ khi đang chơi vui, hay bắt c&ocirc; v&agrave;o nh&agrave; khi đang dạo m&aacute;t giữa vườn trong đ&ecirc;m vắng. Pippi sẵn s&agrave;ng đối đầu với những kẻ bắt nạt, bọn kẻ trộm hay t&ecirc;n hợm hĩnh đ&ograve;i mua biệt thự B&aacute;t nh&aacute;o.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/pippi-tat-dai-tai-ban--1-.jpg" style="width: 371px; height: 480px;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Nhưng cũng ch&iacute;nh từ cuộc sống đ&oacute;, Pippi tự lập hơn mọi đứa trẻ kh&aacute;c. C&ocirc; b&eacute; tất d&agrave;i biết sắp xếp lấy nh&agrave; cửa, nấu ăn rất ngon, ưa chi&ecirc;u đ&atilde;i bạn b&egrave;. Pippi c&oacute; thể tự gội đầu, th&iacute;ch vục cả mặt trong chậu nước; c&oacute; thể vừa c&agrave;i khuy sau của &aacute;o, vừa tết t&oacute;c. Hơn thế, Pippi tất d&agrave;i c&oacute; một t&igrave;nh cảm thật kho&aacute;ng đạt, nồng n&agrave;n với mọi người. C&ocirc; b&eacute; sẵn s&agrave;ng nhảy l&ecirc;n cửa sổ mua vui cho hai anh em h&agrave;ng x&oacute;m bị ốm phải nằm nh&agrave;, c&ocirc; l&agrave;m tiệc c&ugrave;ng c&aacute;c bạn chơi thật vui, v&agrave; v&igrave; họ m&agrave; c&ocirc; ở lại biệt thự B&aacute;t Nh&aacute;o, kh&ocirc;ng l&ecirc;n thuyền với cha nữa.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Pippi tất d&agrave;i - một c&ocirc; b&eacute;, một thế giới lung linh đ&atilde; l&agrave;m nức l&ograve;ng độc giả mọi lứa tuổi ở hơn 100 quốc gia tr&ecirc;n thế giới. H&igrave;nh tượng văn học sống động n&agrave;y đ&atilde; nhiều lần được dựng th&agrave;nh phim.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/pippi-tat-dai-tai-ban--2-.jpg" style="width: 371px; height: 480px;" /></p>\r\n', 'pippi-tat-dai-tai-ban--1-.jpg', 'PIPPI TẤT DÀI', 'PIPPI TẤT DÀI', 1, 6, '2016-02-03 23:42:19', '2016-02-03 23:42:19'),
(9, 'QUYỀN THUẬT VÔ SONG (TẬP 15)', 'quyen-thuat-vo-song-tap-15', 60000, 0, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	<span style="line-height: 18.5714px;">Tr&ecirc;n đường sang th&ocirc;n b&ecirc;n cạnh, Chinmi v&agrave; nh&oacute;m bảo ti&ecirc;u cho cậu qu&aacute; giang đ&atilde; gặp phải một to&aacute;n sơn tặc đ&oacute;ng đinh tại đỉnh n&uacute;i Nhật Lu&acirc;n. Thủ lĩnh của bọn ch&uacute;ng l&agrave; một g&atilde; khổng lồ mặc &aacute;o gi&aacute;p trụ to&agrave;n th&acirc;n c&ugrave;ng vũ kh&iacute; l&agrave; c&acirc;y c&acirc;y kiếm sắt đầy uy lực, kh&ocirc;ng loại vũ kh&iacute; n&agrave;o c&oacute; thể đả thương được hắn. Chinmi lại một lần nữa rơi v&agrave;o bế tắc... Quyền thuật v&ocirc; song - Tekken Chinmi l&agrave; một trong những manga về v&otilde; thuật v&ocirc; c&ugrave;ng đặc sắc, chưa kể c&ograve;n...</span></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	&nbsp;</p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Tr&ecirc;n đường sang th&ocirc;n b&ecirc;n cạnh, Chinmi v&agrave; nh&oacute;m bảo ti&ecirc;u cho cậu qu&aacute; giang đ&atilde; gặp phải một to&aacute;n sơn tặc đ&oacute;ng đinh tại đỉnh n&uacute;i Nhật Lu&acirc;n. Thủ lĩnh của bọn ch&uacute;ng l&agrave; một g&atilde; khổng lồ mặc &aacute;o gi&aacute;p trụ to&agrave;n th&acirc;n c&ugrave;ng vũ kh&iacute; l&agrave; c&acirc;y c&acirc;y kiếm sắt đầy uy lực, kh&ocirc;ng loại vũ kh&iacute; n&agrave;o c&oacute; thể đả thương được hắn. Chinmi lại một lần nữa rơi v&agrave;o bế tắc...</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Quyền thuật v&ocirc; song - Tekken Chinmi l&agrave; một trong những manga về v&otilde; thuật v&ocirc; c&ugrave;ng đặc sắc, chưa kể c&ograve;n l&agrave; một người bạn gắn b&oacute; với tuổi thơ của rất nhiều độc giả.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Phi&ecirc;n bản tankobon (thay v&igrave; bunko) của tập truyện nhằm mang tới một trải nghiệm tuyệt vời nhất c&oacute; thể cho c&aacute;c độc giả y&ecirc;u mến thể loại h&agrave;nh động - v&otilde; thuật n&agrave;y. Chắc chắn đ&acirc;y sẽ l&agrave; phi&ecirc;n bản ho&agrave;n thiện nhất, với chất lượng cũng như nội dung s&aacute;t bản gốc để c&aacute;c fan h&acirc;m mộ &quot;Ho&agrave;ng Phi Hồng&quot; thuở n&agrave;o được sở hữu một t&aacute;c phẩm mang trọn những c&aacute;i hay, c&aacute;i đẹp của tinh thần v&otilde; thuật, quả cảm m&agrave; t&aacute;c giả muốn truyền tải.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/quyen-thuat-vo-song-tap-15--1-.jpg" style="width: 371px; height: 480px;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Nếu bạn thuộc thế hệ độc giả 8x v&agrave; 9x đời đầu th&igrave; chắc hẳn bạn sẽ biết đến series c&aacute;c bộ truyện tranh về Ho&agrave;ng Phi Hồng của họa sĩ Takeshi Maekawa. Những bộ truyện tranh n&agrave;y thường xoay quanh thế giới v&otilde; thuật với nh&acirc;n vật ch&iacute;nh l&agrave; ch&uacute; nh&oacute;c Ho&agrave;ng Phi Hồng c&ugrave;ng cuộc h&agrave;nh tr&igrave;nh tầm sư v&agrave; nghi&ecirc;n cứu v&otilde; thuật của cậu ta.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Được biết, series truyện tranh về ch&uacute; nh&oacute;c Ho&agrave;ng Phi Hồng của t&aacute;c giả Takeshi Maekawa thực sự đ&atilde; để lại được ấn tượng mạnh với nhiều thế hệ độc giả Việt Nam. Truyện được đ&aacute;nh gi&aacute; cao nhờ n&eacute;t vẽ chi tiết, cảnh đấu v&otilde; tuyệt đỉnh c&ugrave;ng sự đa dạng về kiến thức v&otilde; thuật được t&aacute;c giả lồng gh&eacute;p trong từng chương.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img src="https://bizweb.dktcdn.net/thumb/large/100/047/782/products/quyen-thuat-vo-song-tap-15--2-.jpg?v=1452499253510" style="box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;" /></p>\r\n', 'quyen-thuat-vo-song-tap-15--1-.jpg', 'QUYỀN THUẬT VÔ SONG (TẬP 15)', 'QUYỀN THUẬT VÔ SONG (TẬP 15)', 1, 10, '2016-02-03 23:45:30', '2016-02-03 23:45:30'),
(10, 'RUROUNI KENSHIN (TẬP 12)', 'rurouni-kenshin-tap-12', 50000, 35000, '<p>\r\n	<span style="color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">Himura Kenshin l&agrave; một Hitokiri (s&aacute;t thủ) huyền thoại cuối đời Bakumatsu (thời nh&agrave; Mạc), được mệnh danh l&agrave; Hitokiri Battousai, với m&ocirc;n ph&aacute;i &ldquo;Hitten Mitsuru Ryu&rdquo; (hay c&ograve;n gọi l&agrave; Phi Thi&ecirc;n Ngự Kiếm). Anh vung kiếm hy vọng c&oacute; thể x&acirc;y dựng một thời đại mới, đem lại hạnh ph&uacute;c v&agrave; ấm no cho nh&acirc;n d&acirc;n, khi Bakumatsu sụp đổ th&igrave; anh biến mất với lời thề sẽ kh&ocirc;ng bao giờ giết th&ecirc;m bất cứ ai. Anh trở th&agrave;nh một l&atilde;ng kh&aacute;ch đi ngao du thi&ecirc;n hạ, d&ugrave;ng thanh kiếm lưỡi ngược của m&igrave;nh để bảo vệ...</span></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Himura Kenshin l&agrave; một Hitokiri (s&aacute;t thủ) huyền thoại cuối đời Bakumatsu (thời nh&agrave; Mạc), được mệnh danh l&agrave; Hitokiri Battousai, với m&ocirc;n ph&aacute;i &ldquo;Hitten Mitsuru Ryu&rdquo; (hay c&ograve;n gọi l&agrave; Phi Thi&ecirc;n Ngự Kiếm). Anh vung kiếm hy vọng c&oacute; thể x&acirc;y dựng một thời đại mới, đem lại hạnh ph&uacute;c v&agrave; ấm no cho nh&acirc;n d&acirc;n, khi Bakumatsu sụp đổ th&igrave; anh biến mất với lời thề sẽ kh&ocirc;ng bao giờ giết th&ecirc;m bất cứ ai. Anh trở th&agrave;nh một l&atilde;ng kh&aacute;ch đi ngao du thi&ecirc;n hạ, d&ugrave;ng thanh kiếm lưỡi ngược của m&igrave;nh để bảo vệ những người yếu thế.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	10 năm sau, anh gặp Kamiya Kaoru v&agrave; đ&atilde; ở lại v&otilde; đường của c&ocirc;. Anh đ&atilde; sống một thời gian rất &ecirc;m đềm b&ecirc;n Kaoru cho đến khi anh nhận được lời đề nghị &aacute;m s&aacute;t Shishio Makoto, một s&aacute;t thủ v&ocirc; c&ugrave;ng lợi hại v&agrave; độc &aacute;c bậc nhất thời bấy giờ...</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/rurouni-kenshin-tap-12--1-.jpg" style="width: 371px; height: 480px;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	C&acirc;u chuyện bắt đầu khi Kenshin gặp Kaoru ở Tokyo. Kaoru đang truy t&igrave;m một người tự xưng l&agrave; Battousai, v&agrave; ban đầu tin rằng Kenshin ch&iacute;nh l&agrave; người c&ocirc; cần t&igrave;m, nhưng sau khi chứng kiến sự vụng về của Kenshin v&agrave; &quot;sakabato&quot; (Nghịch Nhận Đao - kiếm lưỡi ngược), c&ocirc; thấy rằng anh kh&ocirc;ng thể l&agrave; s&aacute;t thủ huyền thoại đ&oacute; được. Thủ phạm thực sự h&oacute;a ra lại l&agrave; một cựu đệ tử của Kamiya dojo muốn b&aacute;o th&ugrave; sau khi bị đuổi đi. Kaoru bị bắt c&oacute;c v&agrave; giam giữ bởi Battousai giả, nhưng Kenshin đ&atilde; xuất hiện, tiết lộ m&igrave;nh ch&iacute;nh l&agrave; Battousai thật sự, v&agrave; khẳng định điều n&agrave;y bằng việc ti&ecirc;u diện cả t&ecirc;n giả mạo v&agrave; băng của hắn m&agrave; kh&ocirc;ng giết bất cứ ai. Anh sử dụng kiếm thuật thuật xưa đầy sức mạnh gọi l&agrave; &quot;Hiten Mitsurugi-Ryu&quot; (Phi thi&ecirc;n ngự kiếm). Khi nhận được lời đề nghị gi&uacute;p đỡ của v&otilde; đường, Kenshin quyết định tạm dừng việc l&agrave; một Rurouni v&agrave; ở lại dojo.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img alt="" src="http://localhost/booksonline5.0/public/upload/motasanpham/images/rurouni-kenshin-tap-12--2-.jpg" style="width: 371px; height: 480px;" /></p>\r\n', 'rurouni-kenshin-tap-12--1-.jpg', 'RUROUNI KENSHIN (TẬP 12)', 'RUROUNI KENSHIN (TẬP 12)', 1, 10, '2016-02-03 23:47:41', '2016-02-03 23:47:41'),
(11, 'BASARA (TẬP 13)', 'basara-tap-13', 18000, 0, '<p>\r\n	<span style="color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">Basara kh&ocirc;ng chỉ l&agrave; một manga d&agrave;i kỳ ăn kh&aacute;ch của NXB Shogakukan ở thập 90 m&agrave; c&ograve;n l&agrave; t&aacute;c phẩm g&acirc;y tiếng vang lớn ở nhiều quốc gia tại Ch&acirc;u &Aacute;. Truyện kể về một nước Nhật Bản giả tưởng, nơi tồn tại chế độ phong kiến bảo thủ v&agrave; hủ bại. Do đ&oacute; mọi người tin v&agrave;o một lời nguyền xa xưa về một đứa con số phận, người sẽ giải ph&oacute;ng người d&acirc;n khỏi cuộc sống n&ocirc; lệ đ&oacute;i khổ. Theo lời sấm truyền, vị cứu tinh ấy sẽ xuất hiện ở l&agrave;ng Bạch Hổ. V&agrave;...</span></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Basara kh&ocirc;ng chỉ l&agrave; một manga d&agrave;i kỳ ăn kh&aacute;ch của NXB Shogakukan ở thập 90 m&agrave; c&ograve;n l&agrave; t&aacute;c phẩm g&acirc;y tiếng vang lớn ở nhiều quốc gia tại Ch&acirc;u &Aacute;. Truyện kể về một nước Nhật Bản giả tưởng, nơi tồn tại chế độ phong kiến bảo thủ v&agrave; hủ bại. Do đ&oacute; mọi người tin v&agrave;o một lời nguyền xa xưa về một đứa con số phận, người sẽ giải ph&oacute;ng người d&acirc;n khỏi cuộc sống n&ocirc; lệ đ&oacute;i khổ. Theo lời sấm truyền, vị cứu tinh ấy sẽ xuất hiện ở l&agrave;ng Bạch Hổ. V&agrave; một người phụ nữ trong l&agrave;ng đ&atilde; sinh ra một cặp song sinh Tatara - Sarasa, cậu anh Tatara trở th&agrave;nh niềm hi vọng của d&acirc;n l&agrave;ng.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Nhưng Tatara lại bị qu&acirc;n đội của triều đ&igrave;nh s&aacute;t hại v&agrave; thần kiếm Bạch Hổ cũng bị X&iacute;ch Vương chiếm đoạt. V&igrave; vậy c&ocirc; em g&aacute;i Sarasa cắt t&oacute;c giả trang th&agrave;nh anh trai để đứng l&ecirc;n khởi nghĩa, t&igrave;m kiếm đồng minh chống lại triều đ&igrave;nh. Tr&ecirc;n đường đi, Sarasa đ&atilde; phải l&ograve;ng Shuri, một anh ch&agrave;ng đẹp trai c&oacute; th&acirc;n thế b&iacute; ẩn v&agrave; lu&ocirc;n gi&uacute;p đỡ c&ocirc;. Tuy nhi&ecirc;n họ kh&ocirc;ng thể c&oacute; kết th&uacute;c vi&ecirc;n m&atilde;n khi Shuri ch&iacute;nh l&agrave; X&iacute;ch Vương, kẻ th&ugrave; động trời chung với Sarasa.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Truyện tranh Basara từng gi&agrave;nh giải thưởng manga Shogakukan v&agrave;o năm 1992.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img src="https://bizweb.dktcdn.net/thumb/large/100/047/782/products/basara-tap-13--1-.jpg?v=1452500867037" style="box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Chuyện giả tưởng rằng: Sau một thảm họa hủy diệt tr&aacute;i đất v&agrave;o cuối thế kỷ 21, nước Nhật văn minh lừng lẫy biến th&agrave;nh một v&ugrave;ng đất kh&ocirc; cằn với sa mạc m&ecirc;nh m&ocirc;ng. Rồi 300 năm sau đ&oacute;, ho&agrave;ng tộc Saffron lại thiết lập chế độ phong kiến chuy&ecirc;n chế h&agrave; khắc tại đ&acirc;y. Ho&agrave;ng đế Saffron chia quyền cai trị đất nước cho bốn người con trai. V&ugrave;ng đất kh&aacute; m&agrave;u mỡ nhưng cũng đầy loạn lạc ph&iacute;a Bắc giao cho Hắc Vương, người con trai trưởng. V&ugrave;ng chung quanh thủ đ&ocirc; Tokyo được cai quản bởi Thương Vương, người con trai thứ hai. V&ugrave;ng n&ocirc;ng th&ocirc;n giữa thuộc quyền Bạch Vương, người con thứ ba. V&ugrave;ng Kyushu v&agrave; sa mạc ph&iacute;a T&acirc;y l&agrave; l&atilde;nh địa của X&iacute;ch Vương, người con trai &uacute;t. V&ugrave;ng cuối xa mạc ph&iacute;a T&acirc;y n&agrave;y l&agrave; một ng&ocirc;i l&agrave;ng nhỏ Bakyo. Ng&agrave;y nọ, cặp b&eacute; song sinh, một trai Tatara, một g&aacute;i Sarasa c&ugrave;ng mở mắt ch&agrave;o đời. Theo nh&agrave; ti&ecirc;n tri Naji th&igrave; một trong hai người con b&eacute; nhỏ n&agrave;y l&agrave; &quot;Đứa con số mệnh&quot;, l&agrave; người thay đổi vận mệnh nước nh&agrave;. Tất cả d&acirc;n l&agrave;ng đều tin rằng đ&oacute; l&agrave; đứa b&eacute; trai v&agrave; dồn tất cả l&ograve;ng y&ecirc;u thương, qu&yacute; trọng v&agrave; mong đợi v&agrave;o Tatara. Thanh linh kiếm Bakyo được d&acirc;n l&agrave;ng k&iacute;nh cẩn trao cho &ldquo;đứa con số mệnh&rdquo; Tatara v&agrave;o ng&agrave;y lễ trưởng th&agrave;nh (12 tuổi). Trong khi c&ocirc; em g&aacute;i Sarasa, thua buồn cho số phận đứa con thừa của m&igrave;nh đ&atilde; bỏ chạy băng v&agrave;o sa mạc&hellip; V&agrave; c&acirc;u chuyện Basara bi h&ugrave;ng, huyền b&iacute; m&agrave; l&atilde;ng mạn tuyệt vời ấy bắt đầu&hellip;</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img src="https://bizweb.dktcdn.net/thumb/large/100/047/782/products/basara-tap-13--2-.jpg?v=1452500867037" style="box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;" /></p>\r\n', 'basara-tap-13--1-.jpg', 'BASARA (TẬP 13)', 'BASARA (TẬP 13)', 1, 10, '2016-02-03 23:49:34', '2016-02-03 23:49:34');
INSERT INTO `products` (`id`, `name`, `alias`, `price`, `pricesale`, `intro`, `content`, `image`, `keywork`, `discription`, `user_id`, `cate_id`, `created_at`, `updated_at`) VALUES
(12, 'CAT STREET (TẬP 7)', 'cat-street-tap-7', 35000, 0, '<p>\r\n	<span style="color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">Th&agrave;nh lập một c&ocirc;ng ty ri&ecirc;ng, Kouichi đ&atilde; n&oacute;i với Keito rằng: cậu sẽ &quot;tốt nghiệp&quot; khỏi El Liston, Keito đ&aacute;p lại rằng: &quot;Thật phiền phức&quot; v&agrave; quay lưng với Kouichi. Ngay sau đ&oacute;, Rei n&oacute;i chia tay Keito, v&agrave; Keito với suy nghĩ &quot;M&igrave;nh chẳng mang được hạnh ph&uacute;c cho ai cả&quot; mới bị thầy hiệu trưởng mắng l&agrave; &quot;ốc kh&ocirc;ng mang nổi m&igrave;nh ốc, c&ograve;n đ&ograve;i mang cọc cho r&ecirc;u&quot;. Nhờ vậy, Keito đ&atilde; quyết định quay lại giới nghệ sĩ. C&acirc;u chuyện tập trung v&agrave;o Keito Aoyama, một diễn vi&ecirc;n nh&iacute; nổi tiếng l&uacute;c trước....</span></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Th&agrave;nh lập một c&ocirc;ng ty ri&ecirc;ng, Kouichi đ&atilde; n&oacute;i với Keito rằng: cậu sẽ &quot;tốt nghiệp&quot; khỏi El Liston, Keito đ&aacute;p lại rằng: &quot;Thật phiền phức&quot; v&agrave; quay lưng với Kouichi. Ngay sau đ&oacute;, Rei n&oacute;i chia tay Keito, v&agrave; Keito với suy nghĩ &quot;M&igrave;nh chẳng mang được hạnh ph&uacute;c cho ai cả&quot; mới bị thầy hiệu trưởng mắng l&agrave; &quot;ốc kh&ocirc;ng mang nổi m&igrave;nh ốc, c&ograve;n đ&ograve;i mang cọc cho r&ecirc;u&quot;. Nhờ vậy, Keito đ&atilde; quyết định quay lại giới nghệ sĩ.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	C&acirc;u chuyện tập trung v&agrave;o Keito Aoyama, một diễn vi&ecirc;n nh&iacute; nổi tiếng l&uacute;c trước. V&igrave; một v&agrave;i chuyện xảy ra, c&ocirc; rời khỏi thế giới giải tr&iacute; v&agrave; từ bỏ gia nhập v&agrave;o x&atilde; hội xung quanh. 16 tuổi, mỗi ng&agrave;y của Keito tr&ocirc;i qua trong buồn ch&aacute;n v&agrave; kh&ocirc;ng hề c&oacute; mục đ&iacute;ch. Nhưng rồi một ng&agrave;y c&ocirc; gặp một người lạ mặt dẫn c&ocirc; đến El Liston &ndash; một ng&ocirc;i trường kh&ocirc;ng tốn tiền cho những học sinh trung học như ch&iacute;nh c&ocirc;, những người kh&ocirc;ng thuộc về nơi n&agrave;o cả. V&agrave; ở đ&acirc;y Keito dần dần bắt đầu một chương mới của cuộc đời. C&ugrave;ng với sự gi&uacute;p đỡ của người bạn thuở thơ ấu, Taiyou, v&agrave; những người bạn mới Rei, Momiiji, v&agrave; Kouichi, Keito mở l&ograve;ng hơn v&agrave; chấp nhập sự gi&uacute;p đỡ của họ. Vậy c&ocirc; c&oacute; tiếp tục đ&oacute;ng phim trở lại? Liệu c&ocirc; c&oacute; t&igrave;m được những người bạn thực sự? Cả t&igrave;nh y&ecirc;u nữa?</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img src="https://bizweb.dktcdn.net/thumb/large/100/047/782/products/cat-street-tap-7--1-.jpg?v=1452487027980" style="box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Một diễn vi&ecirc;n nh&iacute; thi&ecirc;n t&agrave;i v&agrave; y&ecirc;u th&iacute;ch c&ocirc;ng việc của m&igrave;nh nhưng lại đến với nghiệp diễn bằng những ấn tượng kh&ocirc;ng đẹp v&agrave; kh&aacute; khi&ecirc;n cưỡng: bố mẹ th&uacute;c &eacute;p, bạn b&egrave; xa l&aacute;nh, người bạn duy nhất t&igrave;m được trong thế giới giải tr&iacute; lại l&agrave; người phản bội m&igrave;nh một c&uacute; đau đớn nhất. Vậy n&ecirc;n khi c&uacute; shock đầu ti&ecirc;n xảy đến, Keito, ở lứa tuổi qu&aacute; sớm như vậy dĩ nhi&ecirc;n đ&atilde; kh&ocirc;ng thể chịu được. V&agrave; c&aacute;i t&ecirc;n El Liston hiện ra.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	C&oacute; lẽ đối với Keito, Kouichi, Rei, Momoji, El Liston l&agrave; chốn dung th&acirc;n, l&agrave; nơi để thuộc về. Ở đ&acirc;y bạn sẽ chẳng lo bị d&ograve;m ng&oacute;, kh&ocirc;ng bị những &aacute;nh nh&igrave;n ph&aacute;n x&eacute;t &ndash; bạn được tự do. Nh&igrave;n bốn người xa lạ trở n&ecirc;n th&acirc;n thiết v&agrave; gắn kết với nhau chặt chẽ từ khi c&ograve;n ở c&aacute;i tuổi trung học đến khi lớn l&ecirc;n, mỗi người bước đi tr&ecirc;n con đường ri&ecirc;ng nhưng vẫn kh&ocirc;ng qu&ecirc;n nhau, vẫn hẹn gặp mặt mới thấy được sự đặc biệt của El Liston. V&agrave; đ&oacute; cũng l&agrave; l&iacute; do cả bốn người bạn nhất quyết duy tr&igrave; ng&ocirc;i trường n&agrave;y để cho sẽ c&ograve;n nhiều người hơn t&igrave;m được mục đ&iacute;ch cuộc sống của m&igrave;nh với sự gi&uacute;p đỡ từ El Liston v&agrave; cả những con người ở đ&oacute;.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img src="https://bizweb.dktcdn.net/thumb/large/100/047/782/products/cat-street-tap-7--2-.jpg?v=1452487027980" style="box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;" /></p>\r\n', 'cat-street-tap-7--1-.jpg', 'CAT STREET (TẬP 7)', 'CAT STREET (TẬP 7)', 1, 10, '2016-02-03 23:51:20', '2016-02-03 23:51:20'),
(13, '7 THÓI QUEN ĐỂ THÀNH ĐẠT', '7-thoi-quen-de-thanh-dat', 15000, 0, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Cuộc sống ng&agrave;y c&agrave;ng phức tạp, căng thẳng v&agrave; khắc nghiệt khi con người chuyển từ thời đại c&ocirc;ng nghiệp sang thời đại c&ocirc;ng nghệ th&ocirc;ng tin c&ugrave;ng với c&aacute;c hệ quả của n&oacute;. Nền kinh tế tri thức ra đời, k&egrave;m theo đ&oacute; l&agrave; h&agrave;ng loạt c&aacute;c vấn đề mới l&agrave;m ảnh hưỡng mạnh mẽ đến đời sống x&atilde; hội, đem đến cho con người những ứng dụng t&iacute;ch cực cũng như tạo ra th&ecirc;m một số kh&oacute; khăn v&agrave; th&aacute;ch thức. Những kh&oacute; khăn v&agrave; th&aacute;ch thức ấy kh&ocirc;ng chỉ kh&aacute;c về lượng m&agrave; c&ograve;n kh&aacute;c về chất.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Những thay đổi s&acirc;u sắc của x&atilde; hội v&agrave; c&aacute;c biến động tr&ecirc;n thương trường to&agrave;n cầu trong thời đại kỹ thuật số đ&atilde; khiến nhiều người nghi ngờ t&iacute;nh ph&ugrave; hợp của những nguy&ecirc;n tắc, những th&oacute;i quen được đưa ra trong cuốn s&aacute;ch n&agrave;y. Theo t&aacute;c giả, cuộc sống c&agrave;ng c&oacute; nhiều biến động, những thử th&aacute;ch ch&uacute;ng ta gặp phải c&agrave;ng lớn th&igrave; &quot;7 th&oacute;i quen&quot; c&agrave;ng c&oacute; gi&aacute; trị đối với tất cả mọi người. V&igrave; c&aacute;c kh&oacute; khăn, th&aacute;ch thức lu&ocirc;n tồn tại v&agrave; ng&agrave;y c&agrave;ng phổ biến n&ecirc;n c&aacute;c giải ph&aacute;p đưa ra đều dựa tr&ecirc;n những nguy&ecirc;n tắc mang t&iacute;nh quy luật, hiển nhi&ecirc;n, bất biến, v&agrave; ph&aacute;t triển l&acirc;u d&agrave;i trong lịch sử.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img src="https://bizweb.dktcdn.net/thumb/large/100/047/782/products/7-thoi-quen-de-thanh-dat-tai-ban-2013--1-.jpg?v=1453196758847" style="box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Trong &quot;7 Th&oacute;i quen để th&agrave;nh đạt&quot;, T&aacute;c giả đưa ra một phương thức tiếp cận to&agrave;n diện, thống nhất v&agrave; mang t&iacute;nh nguy&ecirc;n tắc trong việc giải quyết c&aacute;c vấn đề c&aacute; nh&acirc;n v&agrave; nghề nghiệp. Với sự thấu hiểu s&acirc;u sắc v&agrave; những giai thoại đầy &yacute; nghĩa, t&aacute;c giả mở ra cho ch&uacute;ng ta một con đường để từng bước tiến đến một cuộc sống c&ocirc;ng bằng, trung thực, cống hiến v&agrave; tự trọng - những nguy&ecirc;n tắc gi&uacute;p ch&uacute;ng ta dễ d&agrave;ng th&iacute;ch nghi với mọi thay đổi, đồng thời mang đến cho ch&uacute;ng ta tr&iacute; th&ocirc;ng minh v&agrave; sức mạnh để c&oacute; thể tận dụng được mọi cơ hội m&agrave; những thay đổi c&oacute; mang đến.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Cuốn s&aacute;ch &quot;7 Th&oacute;i quen để th&agrave;nh đạt&quot; chắc chắn sẽ mang đến cho bạn một h&agrave;nh tr&igrave;nh học hỏi l&yacute; th&uacute;. H&atilde;y &aacute;p dụng ngay v&agrave; chia sẻ với người th&acirc;n, bạn b&egrave; những điều bạn học được. H&atilde;y nhớ: học m&agrave; kh&ocirc;ng h&agrave;nh th&igrave; kh&ocirc;ng phải l&agrave; học thực, biết m&agrave; kh&ocirc;ng l&agrave;m th&igrave; kh&ocirc;ng thật sự l&agrave; biết.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Sống theo 7 th&oacute;i quen l&agrave; một cuộc đấu tranh kh&ocirc;ng ngừng bởi v&igrave; khi bạn c&agrave;ng tiến bộ th&igrave; bản chất của c&aacute;c th&aacute;ch thức bạn gặp phải cũng thay đổi.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img src="https://bizweb.dktcdn.net/thumb/large/100/047/782/products/7-thoi-quen-de-thanh-dat-tai-ban-2013--2-.jpg?v=1453196758847" style="box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;" /></p>\r\n', '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Cuộc sống ng&agrave;y c&agrave;ng phức tạp, căng thẳng v&agrave; khắc nghiệt khi con người chuyển từ thời đại c&ocirc;ng nghiệp sang thời đại c&ocirc;ng nghệ th&ocirc;ng tin c&ugrave;ng với c&aacute;c hệ quả của n&oacute;. Nền kinh tế tri thức ra đời, k&egrave;m theo đ&oacute; l&agrave; h&agrave;ng loạt c&aacute;c vấn đề mới l&agrave;m ảnh hưỡng mạnh mẽ đến đời sống x&atilde; hội, đem đến cho con người những ứng dụng t&iacute;ch cực cũng như tạo ra th&ecirc;m một số kh&oacute; khăn v&agrave; th&aacute;ch thức. Những kh&oacute; khăn v&agrave; th&aacute;ch thức ấy kh&ocirc;ng chỉ kh&aacute;c về lượng m&agrave; c&ograve;n kh&aacute;c về chất.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Những thay đổi s&acirc;u sắc của x&atilde; hội v&agrave; c&aacute;c biến động tr&ecirc;n thương trường to&agrave;n cầu trong thời đại kỹ thuật số đ&atilde; khiến nhiều người nghi ngờ t&iacute;nh ph&ugrave; hợp của những nguy&ecirc;n tắc, những th&oacute;i quen được đưa ra trong cuốn s&aacute;ch n&agrave;y. Theo t&aacute;c giả, cuộc sống c&agrave;ng c&oacute; nhiều biến động, những thử th&aacute;ch ch&uacute;ng ta gặp phải c&agrave;ng lớn th&igrave; &quot;7 th&oacute;i quen&quot; c&agrave;ng c&oacute; gi&aacute; trị đối với tất cả mọi người. V&igrave; c&aacute;c kh&oacute; khăn, th&aacute;ch thức lu&ocirc;n tồn tại v&agrave; ng&agrave;y c&agrave;ng phổ biến n&ecirc;n c&aacute;c giải ph&aacute;p đưa ra đều dựa tr&ecirc;n những nguy&ecirc;n tắc mang t&iacute;nh quy luật, hiển nhi&ecirc;n, bất biến, v&agrave; ph&aacute;t triển l&acirc;u d&agrave;i trong lịch sử.</p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: center; background-color: rgb(247, 245, 245);">\r\n	<img src="https://bizweb.dktcdn.net/thumb/large/100/047/782/products/7-thoi-quen-de-thanh-dat-tai-ban-2013--1-.jpg?v=1453196758847" style="box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;" /></p>\r\n<p style="box-sizing: border-box; margin: 0px 0px 10px; color: rgb(137, 137, 137); font-family: Roboto, sans-serif; font-size: 13px; line-height: 18.5714px; text-align: justify; background-color: rgb(247, 245, 245);">\r\n	Trong &quot;7 Th&oacute;i quen để th&agrave;nh đạt&quot;, T&aacute;c giả đưa ra một phương thức tiếp cận to&agrave;n diện, thống nhất v&agrave; mang t&iacute;nh nguy&ecirc;n tắc trong việc giải quyết c&aacute;c vấn đề c&aacute; nh&acirc;n v&agrave; nghề nghiệp. Với sự thấu hiểu s&acirc;u sắc v&